import React, { useState, useEffect } from "react";
import { TransportOrder, Vehicle, Driver } from "../types";

const Dashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState<"orders" | "vehicles" | "drivers">("orders");
  const [orders, setOrders] = useState<TransportOrder[]>([]);
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [drivers, setDrivers] = useState<Driver[]>([]);

  useEffect(() => {
    const storedOrders = localStorage.getItem("transportOrders");
    const storedVehicles = localStorage.getItem("vehicles");
    const storedDrivers = localStorage.getItem("drivers");
    if (storedOrders) setOrders(JSON.parse(storedOrders));
    if (storedVehicles) setVehicles(JSON.parse(storedVehicles));
    if (storedDrivers) setDrivers(JSON.parse(storedDrivers));
  }, []);

  // Statystyki dla zleceń
  const activeOrdersCount = orders.filter(
    (order) => order.status === "Oczekujące" || order.status === "W realizacji"
  ).length;
  const todayDeliveriesCount = orders.filter((order) => {
    const deliveryDate = new Date(order.deliveryDate);
    const today = new Date();
    return (
      deliveryDate.getDate() === today.getDate() &&
      deliveryDate.getMonth() === today.getMonth() &&
      deliveryDate.getFullYear() === today.getFullYear()
    );
  }).length;
  const monthlyRevenue = orders
    .filter((order) => {
      const deliveryDate = new Date(order.deliveryDate);
      const today = new Date();
      return (
        deliveryDate.getMonth() === today.getMonth() &&
        deliveryDate.getFullYear() === today.getFullYear()
      );
    })
    .reduce((sum, order) => sum + order.cargoWeight * 0.5, 0);

  // Statystyki dla pojazdów i kierowców
  const availableVehiclesCount = vehicles.filter((vehicle) => vehicle.status === "Dostępny").length;
  const availableDriversCount = drivers.filter((driver) => driver.status === "Dostępny").length;

  return (
    <section className="mb-12">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">Dashboard</h2>
      
      {/* Zakładki */}
      <div className="flex border-b border-gray-200 mb-6">
        <button
          className={`px-6 py-3 text-sm font-medium transition-colors duration-200 ${
            activeTab === "orders"
              ? "border-b-2 border-blue-600 text-blue-600"
              : "text-gray-600 hover:text-blue-600"
          }`}
          onClick={() => setActiveTab("orders")}
        >
          Zlecenia
        </button>
        <button
          className={`px-6 py-3 text-sm font-medium transition-colors duration-200 ${
            activeTab === "vehicles"
              ? "border-b-2 border-blue-600 text-blue-600"
              : "text-gray-600 hover:text-blue-600"
          }`}
          onClick={() => setActiveTab("vehicles")}
        >
          Pojazdu
        </button>
        <button
          className={`px-6 py-3 text-sm font-medium transition-colors duration-200 ${
            activeTab === "drivers"
              ? "border-b-2 border-blue-600 text-blue-600"
              : "text-gray-600 hover:text-blue-600"
          }`}
          onClick={() => setActiveTab("drivers")}
        >
          Kierowcy
        </button>
      </div>

      {/* Zawartość zakładek */}
      {activeTab === "orders" && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <h3 className="text-lg font-medium text-gray-700">Aktywne zlecenia</h3>
            <p className="text-3xl font-bold text-blue-600 mt-2">{activeOrdersCount}</p>
            <p className="text-sm text-gray-500 mt-1">W trakcie realizacji lub oczekujące</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <h3 className="text-lg font-medium text-gray-700">Dzisiejsze dostawy</h3>
            <p className="text-3xl font-bold text-blue-600 mt-2">{todayDeliveriesCount}</p>
            <p className="text-sm text-gray-500 mt-1">Planowane na dziś</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <h3 className="text-lg font-medium text-gray-700">Przychód miesięczny</h3>
            <p className="text-3xl font-bold text-blue-600 mt-2">{monthlyRevenue.toFixed(2)} zł</p>
            <p className="text-sm text-gray-500 mt-1">Obliczone na podstawie wagi</p>
          </div>
        </div>
      )}

      {activeTab === "vehicles" && (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Rejestracja</th>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Typ</th>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Ładowność</th>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Status</th>
              </tr>
            </thead>
            <tbody>
              {vehicles.length === 0 ? (
                <tr>
                  <td colSpan={4} className="p-4 text-center text-gray-500">Brak pojazdów</td>
                </tr>
              ) : (
                vehicles.map((vehicle) => (
                  <tr key={vehicle.id} className="border-t hover:bg-gray-50 transition-colors">
                    <td className="p-4 text-gray-800">{vehicle.registration}</td>
                    <td className="p-4 text-gray-800">{vehicle.type}</td>
                    <td className="p-4 text-gray-800">{vehicle.capacity} kg</td>
                    <td className="p-4">
                      <span
                        className={`inline-block px-2 py-1 text-xs font-medium rounded-full ${
                          vehicle.status === "Dostępny"
                            ? "bg-green-100 text-green-800"
                            : vehicle.status === "W użyciu"
                            ? "bg-yellow-100 text-yellow-800"
                            : "bg-red-100 text-red-800"
                        }`}
                      >
                        {vehicle.status}
                      </span>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
          <div className="p-4 bg-gray-50 border-t">
            <p className="text-sm text-gray-600">
              Dostępne pojazdy: <span className="font-semibold">{availableVehiclesCount}</span>
            </p>
          </div>
        </div>
      )}

      {activeTab === "drivers" && (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Imię i nazwisko</th>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Telefon</th>
                <th className="p-4 text-left text-sm font-semibold text-gray-700">Status</th>
              </tr>
            </thead>
            <tbody>
              {drivers.length === 0 ? (
                <tr>
                  <td colSpan={3} className="p-4 text-center text-gray-500">Brak kierowców</td>
                </tr>
              ) : (
                drivers.map((driver) => (
                  <tr key={driver.id} className="border-t hover:bg-gray-50 transition-colors">
                    <td className="p-4 text-gray-800">{driver.fullName}</td>
                    <td className="p-4 text-gray-800">{driver.phoneNumber}</td>
                    <td className="p-4">
                      <span
                        className={`inline-block px-2 py-1 text-xs font-medium rounded-full ${
                          driver.status === "Dostępny"
                            ? "bg-green-100 text-green-800"
                            : driver.status === "W trasie"
                            ? "bg-yellow-100 text-yellow-800"
                            : "bg-red-100 text-red-800"
                        }`}
                      >
                        {driver.status}
                      </span>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
          <div className="p-4 bg-gray-50 border-t">
            <p className="text-sm text-gray-600">
              Dostępni kierowcy: <span className="font-semibold">{availableDriversCount}</span>
            </p>
          </div>
        </div>
      )}
    </section>
  );
};

export default Dashboard;
