import React, { useState, useEffect } from "react";
import OrderModal from "./OrderModal";
import { TransportOrder, Vehicle, Driver } from "../types";

const Orders: React.FC = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [orders, setOrders] = useState<TransportOrder[]>([]);
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [filter, setFilter] = useState<string>("all");
  const [editingOrder, setEditingOrder] = useState<TransportOrder | null>(null);

  useEffect(() => {
    const storedOrders = localStorage.getItem("transportOrders");
    const storedVehicles = localStorage.getItem("vehicles");
    const storedDrivers = localStorage.getItem("drivers");
    if (storedOrders) setOrders(JSON.parse(storedOrders));
    if (storedVehicles) setVehicles(JSON.parse(storedVehicles));
    if (storedDrivers) setDrivers(JSON.parse(storedDrivers));
  }, []);

  const filteredOrders = filter === "all" ? orders : orders.filter((order) => order.status === filter);

  const addOrUpdateOrder = (order: TransportOrder) => {
    let updatedOrders;
    if (editingOrder) {
      updatedOrders = orders.map((o) => (o.id === order.id ? order : o));
    } else {
      updatedOrders = [...orders, order];
    }
    setOrders(updatedOrders);
    localStorage.setItem("transportOrders", JSON.stringify(updatedOrders));
    setEditingOrder(null);
  };

  const handleEdit = (order: TransportOrder) => {
    setEditingOrder(order);
    setIsModalOpen(true);
  };

  return (
    <section className="mb-8">
      <h2 className="text-xl font-semibold mb-4">Zlecenia transportowe</h2>
      <div className="flex justify-between mb-4">
        <button
          onClick={() => {
            setEditingOrder(null);
            setIsModalOpen(true);
          }}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Dodaj nowe zlecenie
        </button>
        <div className="flex items-center space-x-2">
          <label htmlFor="order-status-filter">Status:</label>
          <select
            id="order-status-filter"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="p-2 border rounded"
          >
            <option value="all">Wszystkie</option>
            <option value="Oczekujące">Oczekujące</option>
            <option value="W realizacji">W realizacji</option>
            <option value="Zakończone">Zakończone</option>
          </select>
        </div>
      </div>
      <table className="w-full bg-white rounded shadow">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">ID</th>
            <th className="p-2">Klient</th>
            <th className="p-2">Trasa</th>
            <th className="p-2">Data</th>
            <th className="p-2">Status</th>
            <th className="p-2">Pojazd</th>
            <th className="p-2">Kierowca</th>
            <th className="p-2">Akcje</th>
          </tr>
        </thead>
        <tbody>
          {filteredOrders.length === 0 ? (
            <tr>
              <td colSpan={8} className="p-4 text-center">Brak zleceń</td>
            </tr>
          ) : (
            filteredOrders.map((order) => (
              <tr key={order.id}>
                <td className="p-2">{order.id}</td>
                <td className="p-2">{order.clientName}</td>
                <td className="p-2">{`${order.pickupAddress} -> ${order.deliveryAddress}`}</td>
                <td className="p-2">{new Date(order.deliveryDate).toLocaleString()}</td>
                <td className="p-2">{order.status}</td>
                <td className="p-2">
                  {order.vehicleId ? vehicles.find((v) => v.id === order.vehicleId)?.registration || "-" : "-"}
                </td>
                <td className="p-2">
                  {order.driverId ? drivers.find((d) => d.id === order.driverId)?.fullName || "-" : "-"}
                </td>
                <td className="p-2">
                  <button
                    onClick={() => handleEdit(order)}
                    className="text-blue-600 hover:underline"
                  >
                    Edytuj
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
      {isModalOpen && (
        <OrderModal
          closeModal={() => {
            setIsModalOpen(false);
            setEditingOrder(null);
          }}
          addOrUpdateOrder={addOrUpdateOrder}
          vehicles={vehicles}
          drivers={drivers}
          editingOrder={editingOrder}
        />
      )}
    </section>
  );
};

export default Orders;
